def SelectionSortStep(list_sort, index):    new_list = []    for i in range(index, len(list_sort)):        if i > index:            new_list.append(list_sort[i])    min_el = new_list.index(min(new_list,default=0))    list_sort[index], list_sort[(min_el+1)+index] = list_sort[(min_el+1)+index], list_sort[index]    return list_sortdef BubbleSortStep(list_sort):    result = True    for j in range(1,len(list_sort)):        i = j - 1        if list_sort[i] > list_sort[j]:            result = False            list_sort[i], list_sort[j] = list_sort[j], list_sort[i]    return result