def ArrayChunk(M: list) -> int:    N_index = len(M)//2    N = M[N_index]    i1 = 0    i2 = len(M) - 1    while True:        while M[i1] < N:            i1 += 1        while M[i2] > N:            i2 = i2 - 1        if i1 == i2-1 and M[i1] > M[i2]:            M[i1], M[i2] = M[i2], M[i1]            if M[i1] == N:                N_index = i1            elif M[i2] == N:                N_index = i2            i1 = 0            i2 = len(M) - 1        elif (i1 == i2) or (i1 == i2-1 or M[i1] < M[i2]):            return N_index        else:            M[i1], M[i2] = M[i2], M[i1]            if M[i1] == N:                N_index = i1            if M[i2] == N:                N_index = i2