def InsertionSortStep(list_sort, step, index):    el = []    for i in range(index, len(list_sort), step):        el.append(i)    if len(el) == 2:        if list_sort[el[0]] > list_sort[el[1]]:            list_sort[el[0]], list_sort[el[1]] = list_sort[el[1]], list_sort[el[0]]    elif len(el) > 2:        num = 0        for j in range(len(el)):            for n in range(j+1, len(el)):                if list_sort[el[j]] > list_sort[el[n]]:                    list_sort[el[j]], list_sort[el[n]] = list_sort[el[n]], list_sort[el[j]]    return list_sortdef KnuthSequence(array_size: int) -> list:    if array_size == 0:        return [1, ]    res = []    i = 1    while i <= array_size:        res.append(i)        i = 3 * i + 1    res.reverse()    return res